import {useState} from 'react';
import {useNavigate} from 'react-router-dom';

export const Page2 = () => {
    const [state, setState] = useState({
        name: '',
        passWord: '',
    });
    const [life, setLife] = useState(100);
    const navigate = useNavigate();
    const handleName = (event: React.ChangeEvent<HTMLInputElement>) => {
        const name = event.target.value;
        setState({...state, name: name});
    };
    const redirectToPage1 = () => {
        navigate('/');
    };
    const redirectToPage3 = () => {
        navigate('/page3');
    };
    const redirectToPage4 = () => {
        navigate('/page4');
    };
    const reDirectResult = () => {
        navigate('/result')
    };
    const logIn = () => {
        if (state.name === 'motoshi' || state.name === 'motosi' || state.name === 'もとし') {
//        確認ダイアログ
            const result = window.confirm('もとしを爆発させますか?');
            if (result) {
                const alert = window.confirm('本当にもとしを爆発させていいですか?');
                if (alert) {
                    window.alert('くたばれ！もとし！！！\r\nもとしに999999ダメージ！！！');
                    setLife(life - 999999);
                    reDirectResult();
                } else {
                    window.alert('キングもとし：「もとし君は俺がいただいた!」');
                    redirectToPage4();
                }
            } else {
                window.alert('殺し屋隼人：「ごめんよ〜」');
                redirectToPage3();
            }
        } else if(state.name.length === 0){
            window.alert('名前を入力してください')
        }else{
            window.alert(`${state.name}の反撃！\r\n${state.name.length * 5}ダメージ`);
            console.log(life);
            setLife(life - state.name.length * 5);
        }
    };
    const handlePass = (event: React.ChangeEvent<HTMLInputElement>) => {
        const pass = event.target.value;
        setState({...state, passWord: pass});
    };

    const hint = () => {
        window.alert('ここを脱出するためには部屋の主を倒そう!')
    };

    return (
        <>
            <div className="Page2">
            </div>
            <p>破壊する相手</p>
            <div>
                <input
                    name="name"
                    value={state.name}
                    onChange={(event) => handleName(event)}
                />
            </div>
            <div>
                <button onClick={logIn}>破壊実行</button>
            </div>
            <p>残り体力 {life}/100</p>
            <button onClick={hint}>ヒント</button>
        </>

    );
};
