import {nameValue} from './component';
import { log } from 'console';

export const App = () => {
    const name = "sakurai"
    const arr = ['inoue',name,'motoshi','king']

    setTimeout(()=>{
        arr[1] = 'debu'
        console.log(name,'name');
console.log(arr[1],'arr[1]');
    },100)

    const [state, setState] = useState({
        name: '',
        passWord: '',
    });

    const handleName = (event: React.ChangeEvent<HTMLInputElement>) => {
        const name = event.target.value;
        setState({...state, name: name});
    };
    const logIn = () => {
        if (state.name === 'inoue' && state.passWord === '0209') {
//        確認ダイアログ
            const result = window.confirm('inoue0209 にログインします');
            if (result) {
                window.alert('debuもとし');
            } else {
                window.alert('殺し屋隼人');
            }
        } else if (state.name !== 'inoue') {
            window.confirm('ユーザー名が違います');

            const gen = () => {
                for (let i = 0;i < 10;i++){
                    if(i % 2 === 0){
                    }
                }
            };

        } else if (state.passWord !== '0209') {
            window.confirm('パスワードが違います');
        }
    };
    const handlePass = (event: React.ChangeEvent<HTMLInputElement>) => {
        const pass = event.target.value;
        setState({...state, passWord: pass});
    };

    return (
        <>
            <div>ユーザー名</div>
            <div>
                <input
                    name="name"
                    value={state.name}
                    onChange={(event) => handleName(event)}
                />
            </div>
            パスワード
            <div>
                <input
                    name="passWord"
                    value={state.passWord}
                    onChange={(event) => handlePass(event)}
                />
            </div>
            <div>
                <button onClick={logIn}>sign in</button>
            </div>

        </>

    );
};

export default App;
