import React, {useState, useEffect} from 'react';
import {useNavigate} from 'react-router-dom';
import {Input, Text, Stack, Heading, Button} from '@chakra-ui/react';
import {deepStrictEqual} from 'node:assert';

interface PokeLanguageType {
    language: {
        name: string;
        url: string;
    };
    name: string;
}

export const Page2 = ({}) => {
    const [value, setValue] = React.useState('');
    const [pokeName, setPokeName] = React.useState([]);
        const promiseArr: Promise<any>[] = [];
        const rand = Math.floor(Math.random() * 500);
        const url = `https://pokeapi.co/api/v2/pokemon/${rand}`;
        promiseArr.push(fetch(url).then((response) => response.json()));
        Promise.all(promiseArr).then(async (result) => {
            const pokemon = result.map((data) => ({
                    name: data.name,
                    id: data.id,
                    ja: data.species
                })
            );
            const pokemonJADetail = pokemon[0].ja.url;
            const getJaName = async (data: RequestInfo | URL) => {
                const fetchPoke: Response = await fetch(data);
                const result = await fetchPoke.json();
                const getUrl = result.url.names.find((name: PokeLanguageType) => name.language.name === 'ja').name;
                setPokeName(getUrl);
            };
               await getJaName(pokemonJADetail);
        });
    const clickEvent = () => {
        if (value.length > 3) {
            window.console.log('gfsdgdsg');
            window.alert(`あなたにピッタリなポケモンは${pokeName}です`);
        } else {
            window.alert('名前を入力してください');
        }
    };
    return (
        <div>
            <Stack spacing={3} width="300px">
                <Input
                    top="100px"
                    value={value}
                    onChange={e => setValue(e.target.value)}
                    placeholder="入力欄"
                    size="md"
                />
                <Button
                    onClick={clickEvent}
                    id="complete" color="#61dafb"
                    top="110px" width="100px"
                >complete</Button>
            </Stack>
        </div>
    );
};
