import React, { useState } from 'react';
import './App.css';

export const App = () => {
  const init = 0;
  const [state, setState] = useState(init);

  const plus = () => {
    setState(state + 1)
  }
  const minus = ()=>{
    if(state === 0){
      setState(10)
    }else{
      setState(state - 1);
    }
  }
  return (
    <div className="App">
      <header className="App-header">
        <>{state}</>
        <button onClick={plus}>+1</button><button onClick={minus}>-1</button>
      </header>
    </div>
  );
}
