import React, { useState } from 'react';
import { HamburgerIcon, CloseIcon } from '@chakra-ui/icons';
import { Box, Button, Drawer, DrawerOverlay, DrawerContent, DrawerCloseButton, DrawerHeader, DrawerBody } from '@chakra-ui/react';
interface HamburgerMenuProps {
}

export const Page1: React.FC<HamburgerMenuProps> = ({}) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    return (
        <Box>
            <Button onClick={toggleMenu}>
                {isOpen ? <CloseIcon /> : <HamburgerIcon />}
            </Button>
            <Drawer placement="right" onClose={toggleMenu} isOpen={isOpen}>
                <DrawerOverlay />
                <DrawerContent>
                    <DrawerCloseButton />
                    <DrawerHeader>Menu</DrawerHeader>
                    <DrawerBody>
                        {/* メニュー項目をここに追加 */}
                        <Button id = 'about' color = 'red'width = '120px'>ABOUT</Button>
                        <Button id = 'people' color = 'red' top = '50px' left = '-120'width = '120px'>PEOPLE</Button>
                        <Button id = 'information'color = 'red' top = '50px' width = '120px'>INFORMATION</Button>
                    </DrawerBody>
                </DrawerContent>
            </Drawer>
        </Box>
    );
};
