import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';
import {Input, Text, Stack, Heading, Button} from '@chakra-ui/react';

interface PokeLanguageType {
    language: {
        name: string;
        url: string;
    };
    name: string;
}

export const Page2 = ({}) => {
    const [value, setValue] = React.useState('');

    const clickEvent = async () => {
        const promiseArr: Promise<any>[] = [];
        const rand = Math.floor(Math.random() * 500);
        const url = `https://pokeapi.co/api/v2/pokemon/${rand}`;
        promiseArr.push(fetch(url).then((response) => response.json()));
        Promise.all(promiseArr).then((result) => {
            const pokemon = result.map((data) => ({
                    name: data.name,
                    id: data.id,
                    ja: data.species
                })
            );
            const getJaName = async () => {
                const fetchPoke: Response = await fetch(pokemon[0].ja.url);
                const toJson = await fetchPoke.json();
                return toJson.url.names.find((name: PokeLanguageType) => name.language.name).name;

            };
            if (value.length > 3) {
                getJaName();
                window.alert(`あなたにピッタリなポケモンは${pokemon[0].ja}です`);
            } else {
                window.alert('名前を入力してください');
            }
        });
    };
    return (
        <div>
            <Stack spacing={3} width="300px">
                <Input
                    top="100px"
                    value={value}
                    onChange={e => setValue(e.target.value)}
                    placeholder="入力欄"
                    size="md"
                />
                <Button
                    onClick={clickEvent}
                    id="complete" color="#61dafb"
                    top="110px" width="100px"
                >complete</Button>
            </Stack>
        </div>
    );
};
