import {useState} from 'react';
import Page1 from './page1';
import {BrowserRouter, Link, Route, Routes} from 'react-router-dom';

export const Page2 = () => {
    const [state, setState] = useState({
        name: '',
        passWord: '',
    });

    const handleName = (event: React.ChangeEvent<HTMLInputElement>) => {
        const name = event.target.value;
        setState({...state, name: name});
    };
    const logIn = () => {
        if (state.name === 'motoshi') {
//        確認ダイアログ
            const result = window.confirm('もとしを爆発させますか?');
            if (result) {
                const alert = window.confirm('本当にもとしを爆発させていいですか?');
                if (alert) {
                    <Link to="/">Page1</Link>;
                    <Routes>
                        <Route path="/" element={<Page1/>}/>
                    </Routes>;
                    window.alert('もとし!大爆発だ!');
                } else {
                    window.alert('キングもとし：「もとし君は俺がいただいた!」');
                }
            } else {
                window.alert('殺し屋隼人：「ごめんよ〜」');
            }
        } else if (state.name !== 'motoshi') {
            window.confirm(`${state.name}を破壊するのはかわいそうです`);
        }
    };
    const handlePass = (event: React.ChangeEvent<HTMLInputElement>) => {
        const pass = event.target.value;
        setState({...state, passWord: pass});
    };

    return (
        <>
            <div className="Page2">
            </div>
            <div>破壊する相手</div>
            <div>
                <input
                    name="name"
                    value={state.name}
                    onChange={(event) => handleName(event)}
                />
            </div>
            <div>
                <button onClick={logIn}>破壊実行</button>
            </div>
        </>

    );
};
