import {useState} from "react";
import "./App.css"
import {validate} from "uuid";

export const App = () => {

  const [state, setState] = useState({
    name: '',
    passWord: '',
  });

  const newAccount = []
  const handleName = (event) => {
    const name = event.target.value
    setState({...state, name: name});
  }
  const logIn = () => {
    if(state.name === 'inoue' && state.passWord === '0209'){
      alert('inoue0209 にログインします')
    }else if(state.name !== 'inoue'){
      alert('ユーザー名が違います')
    }else if(state.passWord !== '0209'){
      alert('パスワードが違います')
    }
  }
  const handlePass = (event) => {
    const pass = event.target.value;
    setState({...state, passWord: pass})
  }
  const createAccount = () => {
    newAccount.push([state.name, state.passWord])
    alert('新たに登録しました')
    console.log(newAccount)
  }
  return (
      <>
        <div>ユーザー名</div>
        <div>
          <input
              name="name"
              value={state.name}
              onChange={(event) => handleName(event)}
          />
        </div>
        パスワード
        <div>
          <input
              name="passWord"
              value={state.passWord}
              onChange={(event) => handlePass(event)}
          />
        </div>
        <div>
          <button onClick={logIn}>sign in</button>
          <button onClick={createAccount}>新規登録</button>
        </div>

      </>

  );
}

export default App;
